"""Flask hello world."""from flask import Flaskfrom flask_login import LoginManager, AnonymousUserMixinfrom src.decorators import login_requiredapp = Flask(__name__)login_manager = LoginManager()login_manager.init_app(app)@login_manager.user_loaderdef load_user(user_id):    """Load user, required by flask-login."""    user = AnonymousUserMixin()    user.is_authenticated = False    return user@app.route("/")def hello_world():    return "<p>Hello, world.</p>"@app.route("/secret")@login_requireddef secret():    """Render secret page."""    return "<p>This is a secret page.</p>"@app.route("/login")def login():    return "<p>Login page.</p>"