"""Flask hello world."""from flask import Flask, request, abortfrom flask_login import LoginManager, AnonymousUserMixinfrom src.decorators import login_required, validate_jsonapp = Flask(__name__)login_manager = LoginManager()login_manager.init_app(app)@login_manager.user_loaderdef load_user(user_id):    """Load user, required by flask-login."""    user = AnonymousUserMixin()    user.is_authenticated = False    return user@app.route("/")def hello_world():    return "<p>Hello, world.</p>"@app.route("/secret")@login_requireddef secret():    """Render secret page."""    return "<p>This is a secret page.</p>"@app.route("/login")def login():    return "<p>Login page.</p>"@app.route("/grade", methods=["POST"])def update_grade():    json_data = request.get_json()    if "student_id" not in json_data:        abort(400)    # update database    return "success!"@app.route("/grade2", methods=["POST"])@validate_json("student_id")def update_grade2():    json_data = request.get_json()    print(json_data)    # update database    return "success!"